property.value:
_G.animations = {
    home_to_back = function()
        geo.tween_system.create(51.84, -145.92, 500, function(value)
            geo.game.menu_title_home.x = value;
        end)
    end,
    back_to_home = function()
        geo.tween_system.create(-145.92, 51.84, 500, function(value)
            geo.game.menu_title_back.x = value;
        end)
    end,
    tween = function(objects_to_tween, tween_for)
        for _, obj_data in ipairs(objects_to_tween) do
            if tween_for and string.find(obj_data.object, tween_for) == nil then
                for i,v in pairs(_G.hide_dimensions) do
                    if v.object == obj_data.object then
                        obj_data.target = v.target
                    end
                end
            end

            local obj = geo.game[obj_data.object]
            local target = obj_data.target

            for prop, target_value in pairs(target) do
                geo.tween_system.create(obj[prop], target_value, 500, function(value)
                    obj[prop] = value
                end)
            end
        end
    end
}

local function deepCopy(table) local copy = {} for k, v in pairs(table) do if type(v) == 'table' then copy[k] = deepCopy(v) else copy[k] = v end end return copy end
_G.original_dimensions = { { object = 'menu_game_1_image', target = {x = geo.game.menu_game_1_image.x, y = geo.game.menu_game_1_image.y, width = geo.game.menu_game_1_image.width, height = geo.game.menu_game_1_image.height} }, { object = 'menu_game_1_shadow', target = {x = geo.game.menu_game_1_shadow.x, y = geo.game.menu_game_1_shadow.y, width = geo.game.menu_game_1_shadow.width, height = geo.game.menu_game_1_shadow.height} }, { object = 'menu_game_1_name', target = {x = geo.game.menu_game_1_name.x, y = geo.game.menu_game_1_name.y, width = geo.game.menu_game_1_name.width, height = geo.game.menu_game_1_name.height, size = geo.game.menu_game_1_name.size} },
